// Get base64 string from webhook body
const base64Data = $input.item.json.body.pdfBase64;
const fileName = $input.item.json.body.fileName || 'cover-letter.pdf';

// Convert base64 to binary
const binaryData = Buffer.from(base64Data, 'base64');

// Return with binary data
return {
  json: {},
  binary: {
    data: {
      data: binaryData.toString('base64'),
      mimeType: 'application/pdf',
      fileName: fileName,
      fileExtension: 'pdf'
    }
  }
};
